(11555, 'wcfm_membership_onapproval_admin_notication_subject', '{site_name}: A vendor application waiting for approval', 'yes'),
(11556, 'wcfm_membership_onapproval_admin_notication_content', '<p>Dear Admin,</p><p>A new vendor <b>{vendor_name}</b> (Store: <b>{vendor_store}</b>) has just applied to our membership plan <b>{membership_plan}</b>.</p><p>Kindly follow the below the link to approve/reject the application.</p><p>Application: {notification_url}</p><p>Thank You</p>', 'yes'),
(11557, 'wcfm_membership_switch_admin_notication_subject', '{site_name}: Vendor Membership Subscription Change', 'yes'),
(11558, 'wcfm_membership_switch_admin_notication_content', '<p>Dear Admin,</p><p><b>{vendor_name}</b> (Store: <b>{vendor_store}</b>) has changed membership plan to <b>{membership_plan}</b>.</p><p>{plan_details}</p><p>Kindly follow the below the link to more details.</p><p>Dashboard: {vendor_url}</p><p>Thank You</p>', 'yes'),
(11559, 'wcfm_membership_switch_notication_subject', '{site_name}: Membership Subscription Successfully Changed', 'yes'),
(11560, 'wcfm_membership_switch_notication_content', '<p>Hi {first_name},</p><p>You have successfully changed membership plan to <b>{membership_plan}</b>.</p><p>Your account already setup and ready to configure.</p><p>{plan_details}</p><p>Kindly follow the below the link to visit your dashboard.</p><p>Dashboard: {dashboard_url}</p><p>Thank You</p>', 'yes'),
(11561, 'wcfm_membership_next_payment_notication_subject', '{site_name}: Membership Subscription Recurring Next Payment', 'yes'),
(11562, 'wcfm_membership_next_payment_notication_content', '<p>Hi {vendor_name},</p><p>Your membership plan (<strong>{membership_plan}</strong>) subscription next billing date <strong>{next_payment_day}</strong>.</p><p>Kindly pay now now to keep your account active.</p><p>Thank You</p>', 'yes'),
(11563, 'wcfm_membership_reminder_notication_subject', '{site_name}: Membership Subscription Renewal Reminder', 'yes'),
(11564, 'wcfm_membership_reminder_notication_content', '<p>Hi {vendor_name},</p><p>Your membership plan (<strong>{membership_plan}</strong>) will expire <strong>{reminder_day}</strong>.</p><p>Kindly renew now to keep your account active.</p><p>Thank You</p>', 'yes'),
(11565, 'wcfm_membership_reject_notication_subject', '{site_name}: Vendor Application Rejected', 'yes'),
(11566, 'wcfm_membership_reject_notication_content', '<p>Hi {first_name},</p><p>Sorry to inform you that, your vendor application has been rejected.</p><p><strong><i>{rejection_reason}</i></strong></p><p>Thank You</p>', 'yes'),
(11567, 'wcfm_membership_cancel_notication_subject', '{site_name}: Membership Subscription Cancelled', 'yes'),
(11568, 'wcfm_membership_cancel_notication_content', '<p>Hi {vendor_name},</p><p>Your membership plan (<strong>{membership_plan}</strong>) has been cancelled.</p><p>Thank You</p>', 'yes'),
(11569, 'wcfm_membership_expire_notication_subject', '{site_name}: Membership Subscription Expired', 'yes'),
(11570, 'wcfm_membership_expire_notication_content', '<p>Hi {vendor_name},</p><p>Your membership plan (<strong>{membership_plan}</strong>) has been expired.</p><p>Thank You</p>', 'yes'),
